// 加入动画
@keyframes outmove {
  0% {
    opacity: 1;
    transform: translateY(0px);
  }
  100% {
    opacity: 0;
    transform: translateY(-20px);
  }
}

@-webkit-keyframes outmove /*Safari and Chrome*/ {
  0% {
    opacity: 1;
    transform: translateY(0px);
  }
  100% {
    opacity: 0;
    transform: translateY(-20px);
  }
}

.upload-container {
  position: relative;
  overflow: hidden;
}

.img-item {
  width: 100px;
  height: 100px;
  border: 1px solid #ddd;
}

.img-hover-container {
  width: 100px;
  height: 100px;
  position: absolute;
  top: 0;
  left: 0;
  display: inline-flex;
  justify-content: center;
  align-items: center;
  color: #fff;
  opacity: 0;
  font-size: 20px;
  background-color: rgba(0, 0, 0, 0.5);
  transition: opacity 0.5s;
  cursor: default;
}

.upload-container:hover .img-hover-container{
  opacity: 1;
}

.img-enter {
  animation: entermove ease 0.8s;
  -webkit-animation: entermove ease 0.8s; /*Safari and Chrome*/
}

.img-out {
  animation: outmove ease 1s;
  -webkit-animation: outmove ease 1s; /*Safari and Chrome*/
}
