@keyframes entermove {
  0% {
    opacity: 0;
    transform: translateY(-20px);
  }
  100% {
    opacity: 1;
    transform: translateY(0px);
  }
}

@-webkit-keyframes entermove /*Safari and Chrome*/ {
  0% {
    opacity: 0;
    transform: translateY(-20px);
  }
  100% {
    opacity: 1;
    transform: translateY(0px);
  }
}

@keyframes outmove {
  0% {
    opacity: 1;
    transform: translateY(0px);
  }
  100% {
    opacity: 0;
    transform: translateY(-20px);
  }
}

@-webkit-keyframes outmove /*Safari and Chrome*/ {
  0% {
    opacity: 1;
    transform: translateY(0px);
  }
  100% {
    opacity: 0;
    transform: translateY(-20px);
  }
}

.upload-container {
  display: flex;
  flex-wrap: wrap;
  margin-bottom: 10px;
}
.img-container {
  display: inline-block;
  position: relative;
  margin-right: 4px;
  width: 100px;
  height: 100px;
  border: 1px solid #c0ccda;
  border-radius: 6px;
  overflow: hidden;
}

.img-title {
  text-align: center;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  font-size: 12px;
}

.img-hover-container {
  position: absolute;
  width: 100%;
  height: 100px;
  left: 0;
  top: 0;
  display: inline-flex;
  justify-content: center;
  align-items: center;
  color: #fff;
  opacity: 0;
  font-size: 20px;
  background-color: rgba(0, 0, 0, 0.5);
  cursor: pointer;
  transition: opacity 0.3s;
}

.img-container:hover .img-hover-container {
  opacity: 1;
}

.img-container-enter {
  animation: entermove ease 0.8s;
  -webkit-animation: entermove ease 0.8s; /*Safari and Chrome*/
}

.img-container-out {
  animation: outmove ease 1s;
  -webkit-animation: outmove ease 1s; /*Safari and Chrome*/
}
